import React from 'react';

const Keyboard = ({ activeNotes, onNoteOn, onNoteOff }) => {
    const startNote = 48; // C3
    const endNote = 84; // C6
    const keys = [];

    const isBlackKey = (note) => {
        const n = note % 12;
        return n === 1 || n === 3 || n === 6 || n === 8 || n === 10;
    };

    for (let i = startNote; i <= endNote; i++) {
        keys.push({
            note: i,
            isBlack: isBlackKey(i),
            label: i % 12 === 0 ? `C${Math.floor(i / 12) - 1}` : null
        });
    }

    return (
        <div className="keyboard-container">
            <div className="keyboard">
                {keys.map((key) => {
                    const isActive = activeNotes.includes(key.note);
                    return (
                        <div
                            key={key.note}
                            className={`key ${key.isBlack ? 'black-key' : 'white-key'} ${isActive ? 'active' : ''}`}
                            onMouseDown={() => onNoteOn(key.note)}
                            onMouseUp={() => onNoteOff(key.note)}
                            onMouseLeave={() => isActive && onNoteOff(key.note)}
                            onTouchStart={(e) => { e.preventDefault(); onNoteOn(key.note); }}
                            onTouchEnd={(e) => { e.preventDefault(); onNoteOff(key.note); }}
                        >
                            {key.label && <span className="note-label">{key.label}</span>}
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

export default Keyboard;
