import React, { useState, useEffect, useRef } from 'react';

function MidiController() {
  const [currentNote, setCurrentNote] = useState(60); // Middle C
  const [midiOutputs, setMidiOutputs] = useState([]);
  const [selectedOutput, setSelectedOutput] = useState(null);
  const [midiAccess, setMidiAccess] = useState(null);
  const [velocity, setVelocity] = useState(64); // Default velocity
  const isInitialized = useRef(false); // Ref to track initialization

  useEffect(() => {
    const getMIDIAccess = async () => {
      try {
        console.log('getting MIDI access');
        const access = await navigator.requestMIDIAccess();
        setMidiAccess(access);
        const outputs = Array.from(access.outputs.values());
        setMidiOutputs(outputs);
        if (outputs.length > 0) {
          setSelectedOutput(outputs[0].id);
        }
      } catch (err) {
        console.error('MIDI access could not be enabled:', err);
      }
    };

    getMIDIAccess();
  }, []);

  useEffect(() => {
    // Play the current note whenever it changes, but not on initialization
    if (isInitialized.current && isInitialized && selectedOutput && midiAccess) {
      const output = midiAccess.outputs.get(selectedOutput);
      if (output) {
        output.send([0x90, currentNote, velocity]); // Note on with current velocity
        setTimeout(() => {
          output.send([0x80, currentNote, 0x40]); // Note off
        }, 500); // Duration of 500ms
      }
    }
    // Set initialized to true after the first render
    isInitialized.current = true;
  }, [currentNote, selectedOutput, midiAccess, velocity]);

  const playNote = (note) => {
    if (selectedOutput && midiAccess) {
      const output = midiAccess.outputs.get(selectedOutput);
      if (output) {
        output.send([0x90, note, velocity]); // Note on with current velocity
        setTimeout(() => {
          output.send([0x80, note, 0x40]); // Note off
        }, 500); // Duration of 500ms
      }
    }
  };

  const handleKeyPress = (event) => {
    const keyMap = {
      'a': -4,
      's': -3,
      'd': -2,
      'f': -1,
      ' ': 0, // Space bar plays the current note
      'j': 1,
      'k': 2,
      'l': 3,
      ';': 4,
    };

    const change = keyMap[event.key];
    if (change !== undefined) {
      setCurrentNote(prevNote => {
        const newNote = Math.max(0, Math.min(prevNote + change, 127)); // Ensure note is within MIDI range
        playNote(newNote); // Play the new note
        return newNote;
      });
    }
  };

  useEffect(() => {
    window.addEventListener('keydown', handleKeyPress);
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [currentNote, selectedOutput]);

  const incrementNote = () => {
    setCurrentNote(prevNote => Math.min(prevNote + 1, 127));
  };

  const decrementNote = () => {
    setCurrentNote(prevNote => Math.max(prevNote - 1, 0));
  };

  return (
    <div className="container mt-4">
      <h1 className="text-center">MIDI Controller</h1>
      <div className="mb-3">
        <label htmlFor="midiOutput" className="form-label">Select MIDI Output:</label>
        <select
          id="midiOutput"
          className="form-select"
          value={selectedOutput || ''}
          onChange={e => setSelectedOutput(e.target.value)}
        >
          <option value="">Select an output</option>
          {midiOutputs.map(output => (
            <option key={output.id} value={output.id}>
              {output.name}
            </option>
          ))}
        </select>
      </div>
      <div className="mb-3">
        <label htmlFor="velocity" className="form-label">Volume (Velocity):</label>
        <input
          id="velocity"
          type="range"
          min="0"
          max="127"
          className="form-range"
          value={velocity}
          onChange={e => setVelocity(Number(e.target.value))}
        />
        <span>{velocity}</span>
      </div>
      <div className="mb-3 text-center">
        <button className="btn btn-primary" onClick={() => playNote(currentNote)}>Play Current Note</button>
        <button className="btn btn-secondary" onClick={incrementNote}>+</button>
      </div>
      <div className="text-center mb-3">Current Note: {currentNote}</div>
      
      {/* Legend Section */}
      <div>
        <legend>Keyboard</legend>
        <table>
          <tr>
            <td>A</td>
            <td>B</td>
            <td>C</td>
            <td>D</td>
            <td>E</td>
            <td>F</td>
            <td>G</td>
            <td>H</td>
          </tr>
          <tr>
            <td colspan="8" style={{width: 100}}> 
              <span title="Decrease by text">[space bar]</span>
            </td>
          </tr>
        </table>
      </div>
    </div>
  );
}

export default MidiController;
