import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useSynth } from './SynthEngine';
import Keyboard from './components/Keyboard';
import Controls from './components/Controls';

function MidiController() {
  const { playNote, stopNote, settings, updateSetting } = useSynth();
  const [midiOutputs, setMidiOutputs] = useState([]);
  const [selectedOutput, setSelectedOutput] = useState(null);
  const [currentNote, setCurrentNote] = useState(60); // Start at Middle C
  const [activeNotes, setActiveNotes] = useState([]); // For visual feedback

  // We need a ref for currentNote to access it inside the event listener closure
  // without constantly re-attaching the listener
  const currentNoteRef = useRef(60);

  useEffect(() => {
    currentNoteRef.current = currentNote;
  }, [currentNote]);

  // MIDI Access Initialization
  useEffect(() => {
    const getMIDIAccess = async () => {
      try {
        const access = await navigator.requestMIDIAccess();
        const outputs = Array.from(access.outputs.values());
        setMidiOutputs(outputs);
        if (outputs.length > 0) {
          setSelectedOutput(outputs[0].id);
        }
      } catch (err) {
        console.warn('MIDI access not available:', err);
      }
    };
    getMIDIAccess();
  }, []);

  const triggerNote = useCallback((note, velocity = 100) => {
    // Visual feedback
    setActiveNotes([note]);

    // Play internal synth
    playNote(note);

    // Send MIDI if output selected
    if (selectedOutput) {
      navigator.requestMIDIAccess().then(access => {
        const output = access.outputs.get(selectedOutput);
        if (output) {
          output.send([0x90, note, velocity]);
          // Note off after a short duration for percussive feel or let user control?
          // For this relative style, usually we want to hold it? 
          // The original code had a timeout for note off. Let's stick to that for now
          // or better, let's handle keyup to stop.
          // But for relative keys, we just trigger.
          setTimeout(() => {
            output.send([0x80, note, 0]);
            stopNote(note);
            setActiveNotes([]);
          }, 500);
        }
      });
    } else {
      // If no MIDI, we still want to stop the synth note after some time 
      // or we could make it monophonic/legato.
      // Let's use a timeout to mimic the original behavior for now.
      setTimeout(() => {
        stopNote(note);
        setActiveNotes([]);
      }, 500);
    }
  }, [playNote, stopNote, selectedOutput]);

  // Relative Key Logic
  useEffect(() => {
    const keyMap = {
      'a': -4,
      's': -3,
      'd': -2,
      'f': -1,
      ' ': 0, // Space bar repeats current
      'j': 1,
      'k': 2,
      'l': 3,
      ';': 4,
    };

    const handleKeyDown = (e) => {
      if (e.repeat) return;

      const interval = keyMap[e.key.toLowerCase()];

      if (interval !== undefined) {
        e.preventDefault(); // Prevent scrolling for space

        const newNote = Math.max(0, Math.min(127, currentNoteRef.current + interval));
        setCurrentNote(newNote);
        triggerNote(newNote);
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [triggerNote]);

  // Allow clicking keyboard to set anchor note
  const handleVisualKeyClick = (note) => {
    setCurrentNote(note);
    triggerNote(note);
  };

  return (
    <div className="synth-container">
      <h1>Neon Rel-Midi</h1>

      <div className="relative-controls-info">
        <div className="key-group left">
          <div className="key-hint">A <span>-4</span></div>
          <div className="key-hint">S <span>-3</span></div>
          <div className="key-hint">D <span>-2</span></div>
          <div className="key-hint">F <span>-1</span></div>
        </div>
        <div className="key-group center">
          <div className="key-hint space">SPACE <span>REPEAT</span></div>
        </div>
        <div className="key-group right">
          <div className="key-hint">J <span>+1</span></div>
          <div className="key-hint">K <span>+2</span></div>
          <div className="key-hint">L <span>+3</span></div>
          <div className="key-hint">; <span>+4</span></div>
        </div>
      </div>

      <div className="current-note-display">
        Current Note: <span>{currentNote}</span>
      </div>

      <Controls
        settings={settings}
        updateSetting={updateSetting}
        midiOutputs={midiOutputs}
        selectedOutput={selectedOutput}
        setSelectedOutput={setSelectedOutput}
      />

      <Keyboard
        activeNotes={activeNotes}
        onNoteOn={handleVisualKeyClick}
        onNoteOff={() => { }} // We handle note off via timeout for now in this mode
      />
    </div>
  );
}

export default MidiController;
