import React, { useState, useEffect } from 'react';
import { WebMidi } from 'webmidi';

function MidiController() {
  const [currentNote, setCurrentNote] = useState(60); // Middle C
  const [midiOutputs, setMidiOutputs] = useState([]);
  const [selectedOutput, setSelectedOutput] = useState(null);

  useEffect(() => {
    WebMidi.enable()
      .then(() => {
        setMidiOutputs(WebMidi.outputs);
        if (WebMidi.outputs.length > 0) {
          setSelectedOutput(WebMidi.outputs[0].id);
        }
      })
      .catch(err => console.error('WebMidi could not be enabled:', err));
  }, []);
  const playCurrentNote = () => {
    if (selectedOutput) {
      const output = WebMidi.getOutputById(selectedOutput);
      output.playNote(currentNote, { duration: 500 });
    }
  };

  useEffect(() => {
    // Play the current note whenever it changes
    if (selectedOutput) {
      const output = WebMidi.getOutputById(selectedOutput);
      output.playNote(currentNote, { duration: 500 });
    }
  }, [currentNote, selectedOutput]); // Dependency array includes currentNote and selectedOutput

  const incrementNote = () => {
    setCurrentNote(prevNote => Math.min(prevNote + 1, 127));
  };

  const decrementNote = () => {
    setCurrentNote(prevNote => Math.max(prevNote - 1, 0));
  };

  return (
    <div>
      <h1>MIDI Controller</h1>
      <div>
        <label htmlFor="midiOutput">Select MIDI Output:</label>
        <select
          id="midiOutput"
          value={selectedOutput || ''}
          onChange={e => setSelectedOutput(e.target.value)}
        >
          <option value="">Select an output</option>
          {midiOutputs.map(output => (
            <option key={output.id} value={output.id}>
              {output.name}
            </option>
          ))}
        </select>
      </div>
      <div>
        <button onClick={playCurrentNote}>Play Current Note</button>
        <button onClick={decrementNote}>-</button>
        <button onClick={incrementNote}>+</button>
      </div>
      <div>Current Note: {currentNote}</div>
    </div>
  );
}

export default MidiController;
